package a

import (
	"context"
	"log/slog"

	"go.uber.org/zap"
)

func demo() {
	logger := zap.NewNop()
	sugar := logger.Sugar()

	slog.Info("User logged in")                                 // want "–ª–æ–≥-—Å–æ–æ–±—â–µ–Ω–∏–µ –¥–æ–ª–∂–Ω–æ –Ω–∞—á–∏–Ω–∞—Ç—å—Å—è —Å–æ —Å—Ç—Ä–æ—á–Ω–æ–π –∞–Ω–≥–ª–∏–π—Å–∫–æ–π –±—É–∫–≤—ã"
	slog.Warn("privet –º–∏—Ä")                                     // want "–ª–æ–≥-—Å–æ–æ–±—â–µ–Ω–∏–µ –¥–æ–ª–∂–Ω–æ —Å–æ–¥–µ—Ä–∂–∞—Ç—å —Ç–æ–ª—å–∫–æ –∞–Ω–≥–ª–∏–π—Å–∫–∏–π —Ç–µ–∫—Å—Ç \\(–∫–∏—Ä–∏–ª–ª–∏—Ü–∞ –∏ –¥—Ä—É–≥–∏–µ –∞–ª—Ñ–∞–≤–∏—Ç—ã –∑–∞–ø—Ä–µ—â–µ–Ω—ã\\)"
	slog.Error("request failed!")                               // want "–ª–æ–≥-—Å–æ–æ–±—â–µ–Ω–∏–µ –Ω–µ –¥–æ–ª–∂–Ω–æ —Å–æ–¥–µ—Ä–∂–∞—Ç—å —Å–ø–µ—Ü—Å–∏–º–≤–æ–ª—ã \\(!, \\?, \\.\\.\\.\\) –∏ —ç–º–æ–¥–∑–∏"
	slog.Debug("token=123")                                     // want "–ª–æ–≥-—Å–æ–æ–±—â–µ–Ω–∏–µ —Å–æ–¥–µ—Ä–∂–∏—Ç –ø–æ—Ç–µ–Ω—Ü–∏–∞–ª—å–Ω–æ —á—É–≤—Å—Ç–≤–∏—Ç–µ–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ"
	slog.InfoContext(context.Background(), "session id leaked") // want "–ª–æ–≥-—Å–æ–æ–±—â–µ–Ω–∏–µ —Å–æ–¥–µ—Ä–∂–∏—Ç –ø–æ—Ç–µ–Ω—Ü–∏–∞–ª—å–Ω–æ —á—É–≤—Å—Ç–≤–∏—Ç–µ–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ"

	logger.Info("api_key leaked")         // want "–ª–æ–≥-—Å–æ–æ–±—â–µ–Ω–∏–µ —Å–æ–¥–µ—Ä–∂–∏—Ç –ø–æ—Ç–µ–Ω—Ü–∏–∞–ª—å–Ω–æ —á—É–≤—Å—Ç–≤–∏—Ç–µ–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ"
	logger.Warn("request...failed")       // want "–ª–æ–≥-—Å–æ–æ–±—â–µ–Ω–∏–µ –Ω–µ –¥–æ–ª–∂–Ω–æ —Å–æ–¥–µ—Ä–∂–∞—Ç—å —Å–ø–µ—Ü—Å–∏–º–≤–æ–ª—ã \\(!, \\?, \\.\\.\\.\\) –∏ —ç–º–æ–¥–∑–∏"
	sugar.Infof("password: %s", "qwerty") // want "–ª–æ–≥-—Å–æ–æ–±—â–µ–Ω–∏–µ —Å–æ–¥–µ—Ä–∂–∏—Ç –ø–æ—Ç–µ–Ω—Ü–∏–∞–ª—å–Ω–æ —á—É–≤—Å—Ç–≤–∏—Ç–µ–ª—å–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ"
	sugar.Infow("hello üòä", "k", "v")      // want "–ª–æ–≥-—Å–æ–æ–±—â–µ–Ω–∏–µ –Ω–µ –¥–æ–ª–∂–Ω–æ —Å–æ–¥–µ—Ä–∂–∞—Ç—å —Å–ø–µ—Ü—Å–∏–º–≤–æ–ª—ã \\(!, \\?, \\.\\.\\.\\) –∏ —ç–º–æ–¥–∑–∏"

	slog.Info("valid message")
	logger.Info("valid message")
	sugar.Infow("valid message", "id", 1)
}
